import React from 'react'
import ReactDOM from 'react-dom'

const Header = (props) =>{
  return(
    <div>
      <h1>{props.course_name} Course</h1>
    </div>
  )
}

 const Content = (props) =>{
   var rows = [];
   for (var i = 0; i < (props.part_name).length; i++){
    rows.push(
    <div>
      <p>Course Part name: {props.part_name[i]}</p>
      <p>Number of exercises: {props.part_exe_num[i]}</p>
    </div>
    )
   }
   return(rows)
  }

  const Total = (props) =>{
    return(
    <div>
      <p>Total number of exercises: {props.exercises_num}</p>
    </div>
    )
  }
const App = () => {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  return(
    <div>
    <Header course_name={course}/>
    <Content part_name={[part1, part2, part3]} part_exe_num={[exercises1, exercises2, exercises3]} />
    <Total exercises_num={exercises1 + exercises2 + exercises3} />
    </div>
